import React, { useState, useEffect } from "react"
import "../App.css"
import {FaSearch} from "react-icons/fa"

function SupervisorProfiles() {
    const [supervisors, setSupervisors] = useState([{}]);

    const [searchTerm, setSearchTerm] = useState("");

    const [filters, setFilters] = useState([{}]);
    const [selectedFilters, setSelectedFilters] = useState([]);

    useEffect(() => {
        fetch("/supervisor-profiles").then(
        res => res.json()
        ).then(
        data => {
            setSupervisors(data.supervisors)
        }
        )
    }, [])

    useEffect(() => {
      fetch("/supervisor-filters").then(
      res => res.json()
      ).then(
      data => {
          setFilters(data.filters)
      }
      )
  }, [])

  const handleFilterClick = (filterTerm) => {
    if (selectedFilters.includes(filterTerm)) {
      setSelectedFilters(selectedFilters.filter((term) => term !== filterTerm));
    } else {
      setSelectedFilters([...selectedFilters, filterTerm]);
    }
 };

  return (
      <div className="page-content">

        <h1 className="page-title">Supervisor Profiles</h1>

        <div className="search-bar-container">
          <div className="search-bar-div">
            <FaSearch id="search-icon" />
            <input  className="search-bar-input" 
                    placeholder="Type to search..."
                    value={searchTerm}
                    onChange={(e) => {setSearchTerm(e.target.value);}}/>
          </div>
        </div>

        <div className="filter-container">
          <p className="filter-by-text">Filter by:</p>
          <div className="filter-buttons-div">
            {filters.map((filterTermObj) => (
                <button
                  key={filterTermObj.filterTerm}
                  className={`filter-button ${selectedFilters.includes(filterTermObj.filterTerm) ? 'selected' : ''}`}
                  onClick={() => handleFilterClick(filterTermObj.filterTerm)}>
                  {filterTermObj.filterTerm}
                </button>
              ))}
          </div>
        </div>

        <div className="supervisor-boxes">
          {supervisors.filter((supervisor) => {
            //TODO: filter through the selectedFilters
                if (searchTerm == "") {
                    console.log(supervisor)
                    return supervisor
                } else if (
                    supervisor.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                    supervisor.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
                    supervisor.projects.toLowerCase().includes(searchTerm.toLowerCase())
                  ) {
                    return supervisor
                }
            }
          ).map((supervisor) => (
            <div key={supervisor.name} className="supervisor-summary">
              <h4 className="supervisor-name">{supervisor.name}</h4>
              <p className="supervisor-email">{supervisor.email}</p>
              <p className="supervisor-projects">{supervisor.projects}</p>
            </div>
          ))}
        </div>
      </div>
  )
}

export default SupervisorProfiles
